function getFacebookData(a){$.ajax({type:"GET",url:serviceData.FB.url,dataType:"xml",contentType:"application/rss+xml"}).done(function(a){localStorage.setItem("Facebook_error",!1),serviceData.FB.error=!1,localStorage.setItem("Facebook",(new XMLSerializer).serializeToString(a)),serviceData.FB.JSON=a,FBHTML()}).fail(function(a,b,c){console.log(a,b,c),localStorage.setItem("Facebook_error",!0),serviceData.FB.error=!0}).always(function(){FBHTML(),a&&a()})}function FBHTML(){if(serviceData.FB.JSON){data=serviceData.FB.JSON;var a="";$(data).find("item").slice(0,25).each(function(){var b=$(this).find("title").text(),c=$(this).find("link").text(),d=moment(new Date($(this).find("pubDate").text()));notificationDate=d.format(moment(d).isSame(moment(),"day")?"hh:mm A":"MMM D, hh:mm A"),a+='<div class="core-item waves-effect"><a href="'+c+'" target="_blank"><div class="notification-title">'+b+'</div><div class="notification-time">'+notificationDate+"</div></a></div>"}),localStorage.setItem("FacebookHTML",a),serviceData.FB.HTML=a}}