function startEffect(a,b){var c=b,d=$('<div class="waves-ripple"></div>');c.append(d);var e=c.offset(),f=a.pageY-e.top,g=a.pageX-e.left;d.css({top:f+"px",left:g+"px","-webkit-transform":"scale(20)",transform:"scale(20)"})}function stopEffect(){$(this).children(".waves-ripple").fadeOut(400,function(){$(this).remove()})}$(document).ready(function(){$("body").on("mousedown",".waves-effect",function(a){var b=$(a.currentTarget);null!==b&&(startEffect(a,b),b.bind("mouseup",stopEffect),b.bind("mouseleave",stopEffect))})});